import React from 'react'
import { Mutation } from "react-apollo";
import gql from 'graphql-tag';

const UPLOAD_FILE = gql`
  mutation CreateAiAnalysisLog($file: Upload!) {
    createAiAnalysisLog(imagePath: $file) {
      imagePath
    }
  }
`

export default function UploadForm() {
  return (
    <Mutation mutation={UPLOAD_FILE}>
      {(createAiAnalysisLog: any) => (
        <div>
          <form
            onSubmit={e => {
              e.preventDefault();
              createAiAnalysisLog({
                variables: { file: input.value }
              });
              input.value = "";
            }}
          >
            <h1>Upload Image</h1>
            <input
              type="file"
              ref={file => {
                input = file;
              }}
            />
            <button type="submit">UploadImage</button>
          </form>
        </div>
      )}
    </Mutation>
  )
}
